mapper.select:
SELECT r.*,a.areaName 
FROM realestate_develop r
join area a
on r.areaId = a.id

	
		r.id = #{id}
	
	
		and r.recordYearMonth = #{recordYearMonth}
	
	
		and r.userId = #{userId}
	
	
		and r.areaId = #{areaId}
	

order by a.displayOrder

mapper.update:
UPDATE realestate_develop
modifyTime=now(), userId=#{userId}, recordYearMonth=#{recordYearMonth}, investHouse=#{investHouse}, investBusiness=#{investBusiness}, investOffice=#{investOffice}, investOther=#{investOther}, financialSourcingInland=#{financialSourcingInland}, financialSourcingForeign=#{financialSourcingForeign}, financialSourcingSelf=#{financialSourcingSelf}, financialSourcingOther=#{financialSourcingOther}, workingAreaHouse=#{workingAreaHouse}, workingAreaBusiness=#{workingAreaBusiness}, workingAreaOffice=#{workingAreaOffice}, workingAreaOther=#{workingAreaOther}, newAreaHouse=#{newAreaHouse}, newAreaBusiness=#{newAreaBusiness}, newAreaOffice=#{newAreaOffice}, newAreaOther=#{newAreaOther}, completeAreaHouse=#{completeAreaHouse}, completeAreaBusiness=#{completeAreaBusiness}, completeAreaOffice=#{completeAreaOffice}, completeAreaOther=#{completeAreaOther}, saledAreaHouse=#{saledAreaHouse}, saledAreaBusiness=#{saledAreaBusiness}, saledAreaOffice=#{saledAreaOffice}, saledAreaOther=#{saledAreaOther}, incomingHouse=#{incomingHouse}, incomingBusiness=#{incomingBusiness}, incomingOffice=#{incomingOffice}, incomingOther=#{incomingOther}, onsaleAreaHouse=#{onsaleAreaHouse}, onsaleAreaBusiness=#{onsaleAreaBusiness}, onsaleAreaOffice=#{onsaleAreaOffice}, onsaleAreaOther=#{onsaleAreaOther}
WHERE id=#{id}

mapper.select:
select 0 as id ,'合计' as district, SUM(r.`investHouse` + r.`investBusiness` + r.`investOffice` + r.`investOther`) AS sumCount, SUM(r.`investHouse`) as investHouseSum, SUM(r.`investBusiness`) as investBusinessSum, SUM(r.`investOffice`) as investOfficeSum, SUM(r.`investOther`) as investOtherSum from realestate_develop r
where r.`recordYearMonth` >= #{startMonth} and r.`recordYearMonth` <= #{endMonth}
union all ( select a.id as id,a.areaName as district, SUM(r.`investHouse` + r.`investBusiness` + r.`investOffice` + r.`investOther`) AS sumCount, SUM(r.`investHouse`) as investHouseSum, SUM(r.`investBusiness`) as investBusinessSum, SUM(r.`investOffice`) as investOfficeSum, SUM(r.`investOther`) as investOtherSum from realestate_develop r left join area a on r.`areaId` = a.id
where r.`recordYearMonth` >= #{startMonth} and r.`recordYearMonth` <= #{endMonth}
group by r.`areaId` order by a.displayOrder)
