mapper.select:
 SELECT
 	id,
 	name,
		password,
 	create_date
 FROM t_user
 
 	
 		id = #{id}
 	
 	
 		AND name = #{name}
 	
 	
 		AND password = #{password}

mapper.update:
 
	UPDATE t_user
	 
		name=#{name}, 
		password=#{password}
	
	WHERE id=#{id}

 
	UPDATE t_user_role SET
	role_id=#{role_id}
	WHERE user_id=#{user_id}
