mapper.select:
	select * from `district_report1`
 
 	
 		id = #{id}
 	
 	
 		and recordYearMonth = #{recordYearMonth}
 	
 	
 		and userId = #{userId}
 	
 
 order by createTime

mapper.update:
UPDATE district_report1
modifyTime=now(), userId=#{userId}, recordYearMonth=#{recordYearMonth}, developerName=#{developerName}, licenceDate=#{licenceDate}, projectName=#{projectName}, location=#{location}, houseNumber=#{houseNumber}, houseArea=#{houseArea}, business=#{business}, office=#{office}, other=#{other}
WHERE id=#{id}

